INSERT INTO `post` VALUES ('16', '附招聘岗位表', '<p>\r\n	&nbsp;</p>\r\n<p>\r\n	附招聘岗位表：</p>\r\n<p>\r\n	1.研发部</p>\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					人数</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					5人</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					岗位要求</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					1.计算机相关专业；</p>\r\n				<p>\r\n					2.熟悉.NET&nbsp;Framework&nbsp;2.0/3.5,&nbsp;对于ADO.NET,&nbsp;Linq,&nbsp;WCF,&nbsp;WinForm&nbsp;等技术熟悉并能灵活运用，C#&nbsp;语言及VS&nbsp;2008&nbsp;IDE运用熟练；</p>\r\n				<p>\r\n					3.具有使用SQL&nbsp;Server&nbsp;2000&nbsp;/&nbsp;2005&nbsp;/2008&nbsp;数据库的应用开发经验；&nbsp;</p>\r\n				<p>\r\n					4.熟悉面向对象设计和开发；&nbsp;</p>\r\n				<p>\r\n					5.具备良好的沟通理解能力，优秀的团队合作精神，能够承受较大的工作压力；</p>\r\n				<p>\r\n					6.具备良好的学习和创新能力、严谨敏捷的思维能力；</p>\r\n				<p>\r\n					符合以下一个或多个条件者优先录用：&nbsp;</p>\r\n				<p>\r\n					1.精通设计模式，并具有成功的实践经验；</p>\r\n				<p>\r\n					2.熟悉常用数据结构，常用算法，编程能力强；</p>\r\n				<p>\r\n					3.大型.net项目开发经验</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					职业规划</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					系统分析师、系统架构师、资深开发工程师</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '2012-11-13 23:53:07', '1', '1', null, '00000000001');
