INSERT INTO `post` VALUES ('21', '招聘招聘', '<p>\r\n	&nbsp;</p>\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					人数</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					5人</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					岗位要求</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					1.计算机及其相关专业；&nbsp;</p>\r\n				<p>\r\n					2.具有一定的英语读写能力，能够阅读相关英文资料；&nbsp;</p>\r\n				<p>\r\n					3.对SaaS，SNS，RIA,DIV+CSS布局，网站SEO，WEB&nbsp;GIS有兴趣；</p>\r\n				<p>\r\n					4.熟悉java&nbsp;script&nbsp;HTML&nbsp;DIV+CSS；</p>\r\n				<p>\r\n					5.了解SOA架构程序开发。</p>\r\n				<p>\r\n					6.语言方面&nbsp;C#,JAVA,&nbsp;AS(FLEX开发)</p>\r\n				<p>\r\n					7.敢于面对困难，承担责任，有较强的独立思考能力，敢于创新，有团队精神；</p>\r\n				<p>\r\n					8.熟练使用Microsoft&nbsp;Visual&nbsp;Studio&nbsp;或&nbsp;Microsoft&nbsp;Expression&nbsp;Studio&nbsp;或&nbsp;Flex&nbsp;Builder；&nbsp;</p>\r\n				<p>\r\n					9.有java&nbsp;script&nbsp;RIA开发经验，有SilverLight&nbsp;RIA开发经验，FLEX&nbsp;RIA开发经验者优先。</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					职业规划</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					前端开发工程师、RIA工程师、网络平台架构师</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '2012-11-13 23:59:53', '1', '1', null, '00000000001');
INSERT INTO `post` VALUES ('22', '软件测试师招聘', '<p>\r\n	&nbsp;</p>\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					人数</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					3人</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					岗位要求</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					1.计算机及其相关专业；&nbsp;</p>\r\n				<p>\r\n					2.具有一定的英语读写能力，能够阅读相关英文资料；&nbsp;</p>\r\n				<p>\r\n					3.对测试工作感兴趣，愿意从事相关工作，且可以胜任重复性工作，细心，有耐心，学习能力强，且能够独立完成自己的工作；</p>\r\n				<p>\r\n					4.熟悉基本的编程语言；</p>\r\n				<p>\r\n					5.责任心强，逻辑推理能力强，有较强的沟通能力、积极的工作态度和团队合作精神；</p>\r\n				<p>\r\n					6.熟练使用MS&nbsp;Office软件（如Word、Excel、Powerpoint）；&nbsp;</p>\r\n				<p>\r\n					7.有软件测试经验，了解软件测试的理论、方法与工具者优先。</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					职业规划</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					测试工程师</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '2012-11-14 00:00:33', '1', '9', null, '00000000001');
