INSERT INTO `post` VALUES ('26', '综合部招聘', '<p>\r\n	&nbsp;</p>\r\n<p>\r\n	.综合部</p>\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					人数</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					3人</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					岗位要求</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					1.计算机及其相关专业；&nbsp;</p>\r\n				<p>\r\n					2.具有一定的英语读写能力，能够阅读相关英文资料；&nbsp;</p>\r\n				<p>\r\n					3.责任心强，有较强的沟通能力、积极的工作态度和团队合作精神；</p>\r\n				<p>\r\n					4.有一定文档写作能力。</p>\r\n				<p>\r\n					5.对数据库管理、系统管理、软件配置有较强的兴趣。</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					职业规划</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					系统管理员，DBA，文档工程师等</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '2012-11-14 00:13:58', '2', '1', null, '00000000001');
INSERT INTO `post` VALUES ('27', '关于郑州轻工业学院期末考试', '<p>\r\n	关于郑州轻工业学院期末考试时间大家想知道吗？</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	关于郑州轻工业学院期末考试<img alt=\"cool\" height=\"20\" src=\"http://localhost:8084/bbs/ckeditor_3.6.2/ckeditor/plugins/smiley/images/shades_smile.gif\" title=\"cool\" width=\"20\" /></p>\r\n<p>\r\n	关于郑州轻工业学院期末考试</p>\r\n<p>\r\n	关于郑州轻工业学院期末考试</p>\r\n<p>\r\n	关于郑州轻工业学院期末考试</p>\r\n<p>\r\n	&nbsp;</p>\r\n', '2012-11-14 00:17:28', '2', '1', null, '00000000002');
INSERT INTO `post` VALUES ('28', '关于UI设计师的招聘岗位介绍', '<p>\r\n	&nbsp;</p>\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					人数</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					2人</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					岗位要求</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p style=\"margin-left:15.8500pt;\">\r\n					1.&nbsp;思维活跃，富有创意者，热爱设计；&nbsp;</p>\r\n				<p style=\"margin-left:15.8500pt;\">\r\n					2.&nbsp;对主流网站的界面及用户交互设计有一定的经验和理解；</p>\r\n				<p style=\"margin-left:15.8500pt;\">\r\n					3.&nbsp;精通Photoshop,coreldraw,Flash等工具；&nbsp;</p>\r\n				<p style=\"margin-left:15.8500pt;\">\r\n					4.&nbsp;对时尚潮流高度敏感，能够灵活调配视觉色彩；</p>\r\n				<p style=\"margin-left:15.8500pt;\">\r\n					5.&nbsp;具有一定的英语读写能力，能够阅读相关英文资料；</p>\r\n				<p style=\"margin-left:15.8500pt;\">\r\n					6.&nbsp;对设计工作感兴趣，愿意从事相关工作，且可以胜任重复性工作，细心，有耐心，学习能力强，且能够独立完成工作；</p>\r\n				<p style=\"margin-left:15.8500pt;\">\r\n					7.&nbsp;责任心强，逻辑推理能力强，有较强的沟通能力、积极的工作态度和团队合作精神；</p>\r\n				<p>\r\n					8.&nbsp;熟悉基本的网页代码。</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:73px;\">\r\n				<p>\r\n					职业规划</p>\r\n			</td>\r\n			<td style=\"width:462px;\">\r\n				<p>\r\n					UI设计师、动漫工程师</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '2012-11-14 00:19:10', '2', '4', null, '00000000002');
