INSERT INTO `post` VALUES ('50', 'Spring3 MVC + jQuery easyUI 做的ajax版本用户管理', '<p>\r\n	[Spring] Spring3 MVC + jQuery easyUI 做的ajax版本用户管理</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	[Spring] Spring3 MVC + jQuery easyUI 做的ajax版本用户管理</p>\r\n<p>\r\n	[Spring] Spring3 MVC + jQuery easyUI 做的ajax版本用户管理</p>\r\n<p>\r\n	[Spring] Spring3 MVC + jQuery easyUI 做的ajax版本用户管理</p>\r\n<p>\r\n	[Spring] Spring3 MVC + jQuery easyUI 做的ajax版本用户管理</p>\r\n<p>\r\n	&nbsp;</p>\r\n', '2012-11-20 23:13:38', '1', '8', null, '00000000007');
INSERT INTO `post` VALUES ('51', 'struts2上传excel文件并解析数据插入数据库', '<p>\r\n	&nbsp;</p>\r\n<div class=\"bar\" style=\"color: rgb(0, 0, 0); font-family: Monaco, \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', Consolas, \'Courier New\', monospace; line-height: 25px; text-align: left; background-color: rgb(239, 239, 239); \">\r\n	<div class=\"tools\" style=\"padding-top: 3px; padding-right: 3px; padding-bottom: 3px; padding-left: 3px; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; font-weight: bold; \">\r\n		Action方法代码&nbsp;<embed allowscriptaccess=\"always\" flashvars=\"clipboard=%40SuppressWarnings(%22unchecked%22)%0A%09public%20String%20uploadAlarmRmsJkmalfuncexperience()%0A%09%7B%0A%09%09String%20date%20%3D%20new%20SimpleDateFormat(%22yyyyMMddHHmmssSS%22).format(new%20Date())%3B%0A%09%09%20ServletContext%20aplication%20%3D%20ServletActionContext.getServletContext()%3B%0A%09%09String%20path%20%3D%20aplication.getRealPath(%22%2Fexcel%22)%3B%0A%09%09System.out.println(path)%3B%0A%09%09FileUtil.copyFileToDir(path%2C%20upload%2C%20date%2B%22_%22%2BuploadFileName)%3B%0A%09%09%0A%09%09try%20%7B%0A%09%09%09InputStream%20is%20%3D%20new%20FileInputStream(new%20File(path%2Cdate%2B%22_%22%2BuploadFileName))%3B%0A%09%09%09try%20%7B%0A%09%09%09%09Workbook%20book%3D%20Workbook.getWorkbook(is)%3B%0A%09%09%09Sheet%20seet%3D%20book.getSheet(0)%3B%0A%09%09%09List%3CAlarmRmsJkmalfuncexperience%3E%20lists%20%3D%20new%20ArrayList()%3B%0A%09%09%09AlarmRmsJkmalfuncexperience%20jkmalfuncexperience%3Dnull%3B%0A%09%09%09for(int%20i%3D1%3Bi%3Cseet.getRows()%3Bi%2B%2B)%0A%09%09%09%7B%0A%09%09%09%09jkmalfuncexperience%20%3Dnew%20AlarmRmsJkmalfuncexperience()%3B%0A%09%09%09%09jkmalfuncexperience.setExpid(seet.getCell(0%2Ci).getContents())%3B%0A%09%09%09%09jkmalfuncexperience.setExptitle(seet.getCell(1%2Ci).getContents())%3B%0A%09%09%09%09jkmalfuncexperience.setMalfuncarea(seet.getCell(2%2Ci).getContents())%3B%0A%09%09%09%09jkmalfuncexperience.setSpecialtype(seet.getCell(3%2Ci).getContents())%3B%0A%09%09%09%09jkmalfuncexperience.setDevicetype(seet.getCell(4%2Ci).getContents())%3B%0A%09%09%09%09jkmalfuncexperience.setDevicecompany(seet.getCell(5%2Ci).getContents())%3B%0A%09%09%09%09jkmalfuncexperience.setVersion(seet.getCell(6%2Ci).getContents())%3B%0A%09%09%09%09jkmalfuncexperience.setExplevel(seet.getCell(7%2Ci).getContents())%3B%0A%09%09%09%09jkmalfuncexperience.setExpkeyword(seet.getCell(8%2Ci).getContents())%3B%0A%09%09%09%09jkmalfuncexperience.setMalfuncdescription(seet.getCell(9%2Ci).getContents())%3B%0A%09%09%09%09jkmalfuncexperience.setExperiencesummary(seet.getCell(10%2Ci).getContents())%3B%0A%09%09%09%09%09%0A%09%09%09%09%09lists.add(jkmalfuncexperience)%3B%0A%09%09%09%7D%0A%09%09%09alarmRmsJkmalfuncexperienceManager.saveOrUpdateAll(lists)%3B%0A%09%09%09%0A%09%09%09%7D%20catch%20(BiffException%20e)%20%7B%0A%09%09%09%09%2F%2F%20TODO%20Auto-generated%20catch%20block%0A%09%09%09%09e.printStackTrace()%3B%0A%09%09%09%7D%20catch%20(IOException%20e)%20%7B%0A%09%09%09%09%2F%2F%20TODO%20Auto-generated%20catch%20block%0A%09%09%09%09e.printStackTrace()%3B%0A%09%09%09%7D%0A%09%09%7D%20catch%20(FileNotFoundException%20e)%20%7B%0A%09%09%09%2F%2F%20TODO%20Auto-generated%20catch%20block%0A%09%09%09e.printStackTrace()%3B%0A%09%09%7D%0A%09%09return%20SUCCESS%3B%0A%09%7D\" height=\"15\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" quality=\"high\" src=\"http://www.iteye.com/javascripts/syntaxhighlighter/clipboard_new.swf\" type=\"application/x-shockwave-flash\" width=\"14\"></embed>&nbsp;<a href=\"http://www.iteye.com/topic/1118037\" style=\"color: rgb(0, 102, 153); \" title=\"收藏这段代码\"><img alt=\"收藏代码\" class=\"star\" src=\"http://www.iteye.com/images/icon_star.png\" style=\"border-top-width: 0px; border-right-width: 0px; border-bottom-width: 0px; border-left-width: 0px; border-style: initial; border-color: initial; border-image: initial; \" /></a></div>\r\n</div>\r\n<ol class=\"dp-default\" start=\"1\" style=\"line-height: 1.4em; margin-top: 0px; margin-right: 0px; margin-bottom: 1px; margin-left: 0px; padding-top: 2px; padding-right: 0px; padding-bottom: 2px; padding-left: 0px; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; border-left-width: 1px; border-top-style: solid; border-right-style: solid; border-bottom-style: solid; border-left-style: solid; border-top-color: rgb(209, 215, 220); border-right-color: rgb(209, 215, 220); border-bottom-color: rgb(209, 215, 220); border-left-color: rgb(209, 215, 220); border-image: initial; list-style-position: initial; list-style-image: initial; background-color: rgb(239, 239, 239); color: rgb(43, 145, 175); font-family: Monaco, \'DejaVu Sans Mono\', \'Bitstream Vera Sans Mono\', Consolas, \'Courier New\', monospace; text-align: left; \">\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">@SuppressWarnings(<span class=\"string\" style=\"color: blue; \">&quot;unchecked&quot;</span>)&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;String&nbsp;uploadAlarmRmsJkmalfuncexperience()&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;date&nbsp;=&nbsp;new&nbsp;SimpleDateFormat(<span class=\"string\" style=\"color: blue; \">&quot;yyyyMMddHHmmssSS&quot;</span>).format(new&nbsp;Date());&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ServletContext&nbsp;aplication&nbsp;=&nbsp;ServletActionContext.getServletContext();&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;String&nbsp;path&nbsp;=&nbsp;aplication.getRealPath(<span class=\"string\" style=\"color: blue; \">&quot;/excel&quot;</span>);&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(path);&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;FileUtil.copyFileToDir(path,&nbsp;upload,&nbsp;date+<span class=\"string\" style=\"color: blue; \">&quot;_&quot;</span>+uploadFileName);&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;try&nbsp;{&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;InputStream&nbsp;is&nbsp;=&nbsp;new&nbsp;FileInputStream(new&nbsp;File(path,date+<span class=\"string\" style=\"color: blue; \">&quot;_&quot;</span>+uploadFileName));&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;try&nbsp;{&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Workbook&nbsp;book=&nbsp;Workbook.getWorkbook(is);&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Sheet&nbsp;seet=&nbsp;book.getSheet(<span class=\"number\" style=\"color: rgb(192, 0, 0); \">0</span>);&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;List&lt;AlarmRmsJkmalfuncexperience&gt;&nbsp;lists&nbsp;=&nbsp;new&nbsp;ArrayList();&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;AlarmRmsJkmalfuncexperience&nbsp;jkmalfuncexperience=null;&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(int&nbsp;i=<span class=\"number\" style=\"color: rgb(192, 0, 0); \">1</span>;i&lt;seet.getRows();i++)&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jkmalfuncexperience&nbsp;=new&nbsp;AlarmRmsJkmalfuncexperience();&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jkmalfuncexperience.setExpid(seet.getCell(<span class=\"number\" style=\"color: rgb(192, 0, 0); \">0</span>,i).getContents());&nbsp;&nbsp;</span></li>\r\n	<li style=\"font-size: 1em; margin-top: 0px; margin-right: 0px; margin-bottom: 0px; margin-left: 38px; padding-top: 0px; padding-right: 0px; padding-bottom: 0px; padding-left: 10px; border-left-width: 1px; border-left-style: solid; border-left-color: rgb(209, 215, 220); background-color: rgb(250, 250, 250); line-height: 18px; \">\r\n		<span style=\"color: black; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;jkmalfuncexperience.setExptitle(seet.getCell(<span class=\"number\" style=\"color: rgb(192, 0, 0); \">1</span>,i).getContents());&nbsp;&nbsp;</span